                    <div className="container">
                        <div classNama="row">
                            <div className="col-6"> 
                                 <img src={confLogo} alt="Logo de " />
                            </div>
                            <div className="col-6 BadgeDetails__hero-attendant-name">
                                <h1>{this.state.data.first}</h1>

                            </div>
                        </div>
                    </div>
                </div>
            </div>

        );
    }  
}
export default BadgeDetails;
